class AutogrowTextarea extends HTMLTextAreaElement{constructor(){super(),this.addEventListener("input",t=>{this.style.height="inherit";let e=window.getComputedStyle(this),r=parseInt(e.getPropertyValue("border-top-width"),10)+this.scrollHeight+parseInt(e.getPropertyValue("border-bottom-width"),10);this.style.height=r+"px"})}}customElements.define("autogrow-textarea",AutogrowTextarea,{extends:"textarea"});