class Autogrow{constructor(t="textarea"){Array.prototype.forEach.call(document.querySelectorAll(t),(t,e)=>{t.addEventListener("input",t=>{this.autoExpand(t.target)})})}autoExpand(t){t.style.height="inherit";let e=window.getComputedStyle(t),r=parseInt(e.getPropertyValue("border-top-width"),10)+t.scrollHeight+parseInt(e.getPropertyValue("border-bottom-width"),10);t.style.height=r+"px"}}export default Autogrow;