class Autogrow{constructor(t="textarea"){let e=document.querySelectorAll(t);for(let t=0;t<e.length;t++){var r=e[t];r.addEventListener("input",t=>{this.autoExpand(t.target)})}}autoExpand(t){t.style.height="inherit";let e=window.getComputedStyle(t),r=parseInt(e.getPropertyValue("border-top-width"),10)+t.scrollHeight+parseInt(e.getPropertyValue("border-bottom-width"),10);t.style.height=r+"px"}}export default Autogrow;